// Define the library as a function that accepts a selector
const simpleDomLibrary = function(selector) {
  // Select the elements using the provided selector
  const elements = document.querySelectorAll(selector);

  // Return an object with methods for manipulating the selected elements
  return {
    // Method to add a class to the selected elements
    addClass: function(className) {
      elements.forEach(element => {
        element.classList.add(className);
      });
      return this; // Return the object to allow method chaining
    },
    // Method to remove a class from the selected elements
    removeClass: function(className) {
      elements.forEach(element => {
        element.classList.remove(className);
      });
      return this; // Return the object to allow method chaining
    },
    // Method to add an event listener to the selected elements
    on: function(eventType, handler) {
      elements.forEach(element => {
        element.addEventListener(eventType, handler);
      });
      return this; // Return the object to allow method chaining
    },
    // Method to add CSS styles to the selected elements
    css: function(styles) {
      elements.forEach(element => {
        for (let property in styles) {
          element.style[property] = styles[property];
        }
      });
      return this; // Return the object to allow method chaining
    }

    // Additional methods for DOM manipulation can be added here
  };
};

